const fill = require('fill-range');
const regex = pm.makeRe('foo/{01..25}/bar', {
  expandRange(a, b) {
    return `(${fill(a, b, { toRegex: true })})`;
  }
});

console.log(regex);
//=> /^(?:foo\/((?:0[1-9]|1[0-9]|2[0-5]))\/bar)$/

console.log(regex.test('foo/00/bar'))  // false
console.log(regex.test('foo/01/bar'))  // true
console.log(regex.test('foo/10/bar')) // true
console.log(regex.test('foo/22/bar')) // true
console.log(regex.test('foo/25/bar')) // true
console.log(regex.test('foo/26/bar')) // false

// strip leading './' from strings
const format = str => str.replace(/^\.\//, '');
const isMatch = picomatch('foo/*.js', { format });
console.log(isMatch('./foo/bar.js')); //=> true

const onMatch = ({ glob, regex, input, output }) => {
  console.log({ glob, regex, input, output });
};

const isMatch = picomatch('*', { onMatch });
isMatch('foo');
isMatch('bar');
isMatch('baz');

const onIgnore = ({ glob, regex, input, output }) => {
  console.log({ glob, regex, input, output });
};

const isMatch = picomatch('*', { onIgnore, ignore: 'f*' });
isMatch('foo');
isMatch('bar');
isMatch('baz');

const onResult = ({ glob, regex, input, output }) => {
  console.log({ glob, regex, input, output });
};

const isMatch = picomatch('*', { onResult, ignore: 'f*' });
isMatch('foo');
isMatch('bar');
isMatch('baz');

const pm = require('picomatch');

// *(pattern) matches ZERO or more of "pattern"
console.log(pm.isMatch('a', 'a*(z)')); // true
console.log(pm.isMatch('az', 'a*(z)')); // true
console.log(pm.isMatch('azzz', 'a*(z)')); // true

// +(pattern) matches ONE or more of "pattern"
console.log(pm.isMatch('a', 'a*(z)')); // true
console.log(pm.isMatch('az', 'a*(z)')); // true
console.log(pm.isMatch('azzz', 'a*(z)')); // true

// supports multiple extglobs
console.log(pm.isMatch('foo.bar', '!(foo).!(bar)')); // false

// supports nested extglobs
console.log(pm.isMatch('foo.bar', '!(!(foo)).!(!(bar))')); // true

listitem.para:
[:punct:] - Punctuation and symbols, equivalent to ‘[\-!"#$%&\’()\\*+,./:;<=>?@[\\]^_<tt>{|}~]</tt>.
* <tt>[:space:]</tt> - Extended space characters, equivalent to <tt>[ \\\\t\\\\r\\\\n\\\\v\\\\f]</tt>.
* <tt>[:upper:]</tt> - Uppercase letters, equivalent to <tt>[A-Z]</tt>.
* <tt>[:word:]</tt> -  Word characters (letters, numbers and underscores), equivalent to <tt>[A-Za-z0-9_]</tt>.
* <tt>[:xdigit:]</tt> - Hexadecimal digits, equivalent to <tt>[A-Fa-f0-9]</tt>.

See the <a href="https://www.gnu.org/software/bash/manual/html_node/Pattern-Matching.html" >Bash Reference Manual</a> for more information.

@subsubsection autotoc_md670 Braces

Picomatch does not do brace expansion. For <a href="https://www.gnu.org/software/bash/manual/html_node/Brace-Expansion.html" >brace expansion</a> and advanced matching with braces, use <a href="https://github.com/micromatch/micromatch" >micromatch</a> instead. Picomatch has very basic support for braces.

@subsubsection autotoc_md671 Matching special characters as literals

If you wish to match the following special characters in a filepath, and you want to use these characters in your glob pattern, they must be escaped with backslashes or quotes:

<strong>Special Characters</strong>

Some characters that are used for matching in regular expressions are also regarded as valid file path characters on some platforms.

To match any of the following characters as literals: `$^*+?()[]

Examples:

@icode{js} 
console.log(pm.makeRe('foo/bar \\(1\\)'));
console.log(pm.makeRe('foo/bar \\(1\\)'));
@endicode 

